  statement {
    effect = "Allow"
    actions = [
      "s3:GetObject",
      "s3:PutObject"
    ]
    resources = [
      "arn:aws:s3:::${var.output_bucket}/*",
      "arn:aws:s3:::${var.working_bucket}/*"
    ]
  }

  # DynamoDB Access
  statement {
    effect = "Allow"
    actions = [
      "dynamodb:GetItem",
      "dynamodb:PutItem",
      "dynamodb:UpdateItem"
    ]
    resources = [
      "arn:aws:dynamodb:${data.aws_region.current.name}:${data.aws_caller_identity.current.account_id}:table/${var.tracking_table}"
    ]
  }

  # AppSync Access (if configured)
  dynamic "statement" {
    for_each = var.appsync_api_arn != "" ? [1] : []

    content {
      effect = "Allow"
      actions = [
        "appsync:GraphQL"
      ]
      resources = ["${var.appsync_api_arn}/*"]
    }
  }

  # KMS Access
  statement {
    effect = "Allow"
    actions = [
      "kms:Decrypt",
      "kms:GenerateDataKey"
    ]
    resources = [var.customer_managed_key_arn]
  }

  # SQS DLQ Access
  statement {
    effect = "Allow"
    actions = [
      "sqs:SendMessage"
    ]
    resources = [aws_sqs_queue.dlq.arn]
  }

