resource "aws_sfn_state_machine" "pattern2_workflow" {
  name     = var.state_machine_name
  role_arn = aws_iam_role.state_machine.arn

  definition = jsonencode({
    Comment = "Pattern 2 IDP Workflow - OCR, Classification, and Extraction"
    StartAt = "OCRStep"
    States = {
      OCRStep = {
        Type     = "Task"
        Resource = var.ocr_function_arn
        Retry = [
          {
            ErrorEquals     = ["Lambda.ServiceException", "Lambda.AWSLambdaException", "Lambda.SdkClientException", "Lambda.TooManyRequestsException"]
            IntervalSeconds = 2
            MaxAttempts     = 6
            BackoffRate     = 2
          }
        ]
        Catch = [
          {
            ErrorEquals = ["States.ALL"]
            Next        = "FailState"
            ResultPath  = "$.error"
          }
        ]
        Next = "ClassificationStep"
      }

      ClassificationStep = {
        Type     = "Task"
        Resource = var.classification_function_arn
        Retry = [
          {
            ErrorEquals     = ["Lambda.ServiceException", "Lambda.AWSLambdaException", "Lambda.SdkClientException", "Lambda.TooManyRequestsException"]
            IntervalSeconds = 2
            MaxAttempts     = 8
            BackoffRate     = 2
          }
        ]
        Catch = [
          {
            ErrorEquals = ["States.ALL"]
            Next        = "FailState"
            ResultPath  = "$.error"
          }
        ]
        Next = "ProcessPageGroups"
      }

      ProcessPageGroups = {
        Type = "Map"
        ItemsPath = "$.sections"
        MaxConcurrency = 10
        Iterator = {
          StartAt = "ExtractionStep"
          States = {
            ExtractionStep = {
              Type     = "Task"
              Resource = var.extraction_function_arn
              Retry = [
                {
                  ErrorEquals     = ["Lambda.ServiceException", "Lambda.AWSLambdaException", "Lambda.SdkClientException", "Lambda.TooManyRequestsException"]
                  IntervalSeconds = 2
                  MaxAttempts     = 10
                  BackoffRate     = 2
                }
              ]
              Catch = [
                {
                  ErrorEquals = ["States.ALL"]
                  Next        = "ExtractionFailState"
                  ResultPath  = "$.error"
                }
              ]
              End = true
            }

            ExtractionFailState = {
              Type = "Fail"
              Cause = "Extraction step failed"
            }
          }
        }
        Catch = [
          {
            ErrorEquals = ["States.ALL"]
            Next        = "FailState"
            ResultPath  = "$.error"
          }
        ]
        Next = "ProcessResultsStep"
      }

      ProcessResultsStep = {
        Type     = "Task"
        Resource = var.process_results_function_arn
        Retry = [
          {
            ErrorEquals     = ["Lambda.ServiceException", "Lambda.AWSLambdaException", "Lambda.SdkClientException", "Lambda.TooManyRequestsException"]
            IntervalSeconds = 2
            MaxAttempts     = 6
            BackoffRate     = 2
          }
        ]
        Catch = [
          {
            ErrorEquals = ["States.ALL"]
            Next        = "FailState"
            ResultPath  = "$.error"
          }
        ]
        End = true
      }

      FailState = {
        Type  = "Fail"
        Cause = "Workflow execution failed"
      }
    }
  })

  logging_configuration {
    log_destination        = "${aws_cloudwatch_log_group.step_functions.arn}:*"
    include_execution_data = true
    level                  = "ERROR"
  }

  tracing_configuration {
    enabled = true
  }

  tags = merge(
    var.tags,
    {
      Name = var.state_machine_name
      Type = "StateMachine"
    }
  )
}

